namespace Milople\Personalizedcool\Block\Adminhtml\Image;

use Magento\Backend\Block\Widget\Context;
use Magento\Backend\Block\Widget\Form\Container;
use Magento\Framework\Phrase;
use Magento\Framework\Registry;

class Edit extends Container
{
    /**
     * Core registry
     *
     * @var Registry
     */
    protected $coreRegistry = null;

    /**
     * @param Context $context
     * @param Registry $registry
     * @param array $data
     */
    public function __construct(
        Context $context,
        Registry $registry,
        array $data = []
    ) {
        $this->coreRegistry = $registry;
        parent::__construct($context, $data);
    }

    /**
     * @return void
     */
    protected function _construct()
    {
        $this->_objectId = 'image_id';
        $this->_blockGroup = 'Milople_Personalizedcool';
        $this->_controller = 'adminhtml_image';
        parent::_construct();
        $this->buttonList->update('save', 'label', __('Save Image'));
        $this->buttonList->update('delete', 'label', __('Delete Image'));

        $this->buttonList->add(
            'saveandcontinue',
            [
                'label' => __('Save and Continue Edit'),
                'class' => 'save',
                'data_attribute' => [
                    'mage-init' => ['button' => ['event' => 'saveAndContinueEdit', 'target' => '#edit_form']],
                ]
            ],
            -100
        );
    }

    /**
     * Get edit form container header text
     *
     * @return Phrase
     */
    public function getHeaderText()
    {
        if ($this->coreRegistry->registry('personalized_category_image')->getId()) {
            return __("Edit Image '%1'", $this->escapeHtml($this->coreRegistry->registry('personalized_category_image')->getName()));
        } else {
            return __('New Image');
        }
    }
}